#include <stdio.h>
#include <math.h>

double Q = 0.000001;
double R = 0.01;
double P = 1, X = 0, K;
int result;

void measurementUpdate(){
	
 	K = (P + Q) / (P + Q + R);
 	P = R * (P + Q) / (R + P + Q);
}


double update(double measurement){
	
	 measurementUpdate();
 	double result = X + (measurement - X) * K;
 	X = result;
 	return result;
}

